import React from "react";
import Title from './components/Title';
import ShoppingList from './components/ShoppingList';
import styles from './App.module.css';
import './App.css';

class App extends React.Component {
  constructor(props)
  {
    super(props);

    this.state = {
      items: [
        { id: 1, value: 'Milk', qty: 5, unit: 'ltr' },
        { id: 2, value: 'Bananas', qty: 6, unit: 'pcs' },
        { id: 3, value: 'Bread', qty: 3, unit: 'x' },
        { id: 4, value: 'Eggs', qty: 16, unit: 'x' }
      ]
    };



  const addAsia = (items) => {
      let newElement = {...items}
      newElement.qty = newElement.qty+1;
      newShoppingListItems = newElement;
      setShoppingListItems(newShoppingListItems);
  }
}



  render()
  {
    const { applicationDescription, applicationName } = this.props;
    return <div className={ styles.shoppingList }>
      <Title 
        applicationDescription={ applicationDescription }
        applicationName={ applicationName }
      />
      <ShoppingList items={ this.state.items } />


      <button onClick={() => addAsia("Milk") }> Milk</button>
      <button onClick={() => addAsia("Bananas") }> Bananas</button>
      <button onClick={() => addAsia("Bread") }> Bread</button>
      <button onClick={() => addAsia("Egg") }> Egg</button>
    </div>
  }

}

export default App;
